import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const EditKategori = () => {
  const [kategori, setKategori] = useState({
    name: '',
    description: ''
  });

  const navigate = useNavigate();
  const {id} = useParams();

  useEffect(() => {
    axios.get('http://localhost:8081/getKategori/'+id)
      .then(res => {
        const { name, description } = res.data.Result;
        setKategori({ name, description });
      })
      .catch(err => console.log(err));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.put('http://localhost:8081/updateKategori/'+id, kategori)
      .then(res => {
        if (res.data.Status === "Success") {
          navigate('/kategorite');
        }
      })
      .catch(err => console.log(err));
  };

  return (
    <div className="d-flex flex-column align-items-center pt-4">
      <h2>Update Kategori</h2>
      <form className="row g-3 w-50" onSubmit={handleSubmit}>
        <div className="col-12">
          <label htmlFor="inputName" className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            id="inputName"
            placeholder="Enter Name"
            autoComplete="off"
            value={kategori.name}
            onChange={e => setKategori({ ...kategori, name: e.target.value })}
          />
        </div>
        <div className="col-12">
          <label htmlFor="inputDescription" className="form-label">Description</label>
          <input
            type="text"
            className="form-control"
            id="inputDescription"
            placeholder="Enter Description"
            autoComplete="off"
            value={kategori.description}
            onChange={e => setKategori({ ...kategori, description: e.target.value })}
          />
        </div>
        <div className="col-12">
          <button type="submit" className="btn btn-primary">Update</button>
        </div>
      </form>
    </div>
  );
};

export default EditKategori;
